"use strict";

var _util = require("@polkadot/util");

var _wasmCrypto = require("@polkadot/wasm-crypto");

var _ = require("./");

// Copyright 2017-2020 @polkadot/util-crypto authors & contributors
// This software may be modified and distributed under the terms
// of the Apache-2.0 license. See the LICENSE file for details.
describe('mnemonicToBip39Seed', () => {
  it('generates a valid seed', () => {
    expect((0, _util.u8aToHex)((0, _.mnemonicToBip39)('seed sock milk update focus rotate barely fade car face mechanic mercy'))).toEqual('0x3c121e20de068083b49c2315697fb59a2d9e8643c24e5ea7628132c58969a027');
  });
  it('generates the seed via WASM', async () => {
    await (0, _wasmCrypto.waitReady)();
    expect((0, _util.u8aToHex)((0, _.mnemonicToBip39)('seed sock milk update focus rotate barely fade car face mechanic mercy'))).toEqual('0x3c121e20de068083b49c2315697fb59a2d9e8643c24e5ea7628132c58969a027');
  });
});