describe('base32Decode', () => {
  it('decodes a base32', () => {
    expect((0, _util.u8aToString)((0, _.base32Decode)('irswgzloorzgc3djpjssazlwmvzhs5dinfxgoijb'))).toEqual('Decentralize everything!!');
  });
  it('decodes a base32 (ipfsCompat)', () => {
    expect((0, _util.u8aToString)((0, _.base32Decode)('birswgzloorzgc3djpjssazlwmvzhs5dinfxgoijb', true))).toEqual('Decentralize everything!!');
  });
});