describe('base64Decode', () => {
  it('decodes a mixed base64 utf8 string', () => {
    expect((0, _.base64Decode)('aGVsbG8gd29ybGQg0J/RgNC40LLQtdGC0YHRgtCy0YPRjiDQvNC4IOS9oOWlvQ==')).toEqual((0, _util.stringToU8a)('hello world Приветствую ми 你好'));
  });
});