describe('TextEncoder', () => {
  let origTE;
  beforeEach(() => {
    // eslint-disable-next-line @typescript-eslint/no-explicit-any,@typescript-eslint/no-unsafe-assignment,@typescript-eslint/no-unsafe-member-access
    origTE = global.TextEncoder;
  });
  afterEach(() => {
    // eslint-disable-next-line @typescript-eslint/no-explicit-any,@typescript-eslint/no-unsafe-assignment,@typescript-eslint/no-unsafe-member-access
    global.TextEncoder = origTE;
  });
  it('polyfills with no exceptions (without TextEncoder)', () => {
    // eslint-disable-next-line @typescript-eslint/no-explicit-any,@typescript-eslint/no-unsafe-assignment,@typescript-eslint/no-unsafe-member-access
    global.TextEncoder = null; // eslint-disable-next-line @typescript-eslint/no-var-requires

    expect(require('./textEncoder')).toBeDefined(); // eslint-disable-next-line @typescript-eslint/no-explicit-any,@typescript-eslint/no-unsafe-assignment,@typescript-eslint/no-unsafe-member-access

    expect(global.TextEncoder).toBeDefined();
  });
  it('polyfills with no exceptions (with TextEncoder)', () => {
    // eslint-disable-next-line @typescript-eslint/no-explicit-any,@typescript-eslint/no-var-requires,@typescript-eslint/no-unsafe-assignment,@typescript-eslint/no-unsafe-member-access
    global.TextEncoder = require('util').TextEncoder; // eslint-disable-next-line @typescript-eslint/no-var-requires

    expect(require('./textEncoder')).toBeDefined(); // eslint-disable-next-line @typescript-eslint/no-explicit-any,@typescript-eslint/no-unsafe-assignment,@typescript-eslint/no-unsafe-member-access

    expect(global.TextEncoder).toBeDefined();
  });
});